import Navbar from "./components/Navbar";
import Sidebar from "./components/Sidebar";
import { useLocation } from "react-router-dom";



const Layout = ({ children }) => {
   const location = useLocation();

   let title = "";
  let description = "";

  if (location.pathname === "/dashboard") {
    title = "Bienvenue sur RED Produit";
    description = "Tableau de bord de gestion générale";
  } else if (location.pathname === "/hotels") {
    title = "Liste des Hôtels";
    description = "Consultez et gérez tous les hôtels enregistrés";
  }

  return (
    <div className="d-flex" style={{ minHeight: "100vh" }}>
      
      {/* Sidebar gauche */}
     <div
        style={{ width: "300px", minWidth: "300px" }} // <- largeur fixe
        className="bg-dark d-flex flex-column"
      >
      <Sidebar />
</div>


      {/* Zone droite */}
      <div className="flex-grow-1 d-flex flex-column">
        
        {/* Navbar en haut */}
        <div style={{ height: "70px" }}>
          <Navbar title={title}/>

           <p className="mb-0">
            <span className="h3 text-dark" style={{ fontWeight: "100" }}>
              {title}
            </span>
            <br />
            {description}
        </p>

          {children}

        </div>

        {/* Contenu scrollable */}
        <div className="flex-grow-1 p-4 bg-light">
          {children}
        </div>

      </div>
    </div>
  );
};

export default Layout;
