  const stats = [
    { title: "Formulaires", value: 125, icon: "üìÑ" },
    { title: "Messages", value: 40, icon: "üí¨" },
    { title: "Emails", value: 25, icon: "üìß" },
    { title: "H√¥tels", value: 40, icon: "üè®" },
  ];

  return (
    <div>
      <h2 className="mb-4">Dashboard</h2>

      <div className="row">
        {stats.map((item, index) => (
          <div className="col-md-6 mb-3" key={index}>
            <div className={`card text-dark  h-100`}>
              <div className="card-body">
                
                  <div className="d-flex text-secondary gap-2">
                    <div style={{ fontSize: "30px", border:"1px solid", borderRadius:"30px",  }}>{item.icon}</div>
                    <div className="d-flex flex-column">
                     <h2>{item.value}</h2>
                   
                     <p className="card-title">{item.title}</p>
                     <p>  je ne sais pas quoi mettre </p>
                   </div>
                  </div>
                  
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
