import { useState } from "react";

const AddHotelModal = ({ show, onClose, onAdd }) => {
  const [nom, setNom] = useState("");
  const [ville, setVille] = useState("");
  const [prix, setPrix] = useState("");
  const [etoiles, setEtoiles] = useState("1");

  if (!show) return null;

  const handleSubmit = () => {
    if (!nom || !ville || !prix) return alert("Remplissez tous les champs !");

    const newHotel = {
      id: Date.now(),
      nom,
      ville,
      prix,
      etoiles: Number(etoiles),
      image: "/images/default-hotel.jpg", // image par défaut
    };

    onAdd(newHotel);
    onClose();

    // Reset formulaire
    setNom("");
    setVille("");
    setPrix("");
    setEtoiles("1");
  };

  return (
    <div
      className="modal fade show d-block"
      style={{ background: "rgba(0,0,0,0.5)" }}
    >
      <div className="modal-dialog">
        <div className="modal-content">

          <div className="modal-header">
            <h5 className="modal-title">Créer un nouvel hôtel</h5>
            <button className="btn-close" onClick={onClose}></button>
          </div>

          <div className="modal-body">
            <div className="mb-3">
              <label>Nom de l'hôtel</label>
              <input
                className="form-control"
                value={nom}
                onChange={(e) => setNom(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label>Ville</label>
              <input
                className="form-control"
                value={ville}
                onChange={(e) => setVille(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label>Prix</label>
              <input
                type="number"
                className="form-control"
                value={prix}
                onChange={(e) => setPrix(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label>Étoiles</label>
              <select
                className="form-control"
                value={etoiles}
                onChange={(e) => setEtoiles(e.target.value)}
              >
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
              </select>
            </div>
          </div>

          <div className="modal-footer">
            <button className="btn btn-secondary" onClick={onClose}>
              Annuler
            </button>
            <button className="btn btn-primary" onClick={handleSubmit}>
              Enregistrer
            </button>
          </div>

        </div>
      </div>
    </div>
  );
};

export default AddHotelModal;
