import { useState, useRef } from "react";

const AddHotelModal = ({ show, onClose, onAdd }) => {
  const [nom, setNom] = useState("");
  const [email, setEmail] = useState("");
  const [prix, setPrix] = useState("");
  const [adresse, setAdresse] = useState("");
  const [numéro, setNuméro] = useState("");
  const [devise, setDevise] = useState("F XOF");
  const [image, setImage] = useState(null);

  if (!show) return null;

  const handleSubmit = () => {
    if (!nom || !email ||  !prix || !devise || !adresse || !numéro) {
      alert("Veuillez remplir tous les champs");
      return;
    }

    const newHotel = {
      id: Date.now(),
      nom,
      email,
      adresse,
      numéro,
      prix,
      devise,
      image: image ? URL.createObjectURL(image) : "/images/default-hotel.jpg",
    };

    onAdd(newHotel);
    onClose();

    // Reset
    setNom("");
    setPrix("");
    setDevise("F XOF");
    setImage(null);
    setEmail("");
    setAdresse("");
    setNuméro("");
  };


  return (
    <div className="modal fade show d-block" style={{ background: "rgba(0,0,0,0.5)" }}>
      <div className="modal-dialog">
        <div className="modal-content">

          <div className="modal-header">
            <h5 className="modal-title">CREER UN NOUVEAU HOTEL</h5>
            <button className="btn-close" onClick={onClose}></button>
          </div>

          <div className="modal-body d-flex gap-4">
          <div>
             <div className="mb-3">
              <label>Nom de l'hôtel</label>
              <input className="form-control" 
              value={nom} onChange={(e) => setNom(e.target.value)}/>
            </div>

            <div className="mb-3">
              <label>E-mail</label>
              <input className="form-control" 
              value={email} onChange={(e) => setEmail(e.target.value)}/>
            </div>

            <div className="mb-3">
              <label>Prix par nuit</label>
              <input type="number" className="form-control" 
              value={prix} onChange={(e) => setPrix(e.target.value)} />
            </div>

          
          </div>

          <div>
            <div className="mb-3">
              <label>Adresse</label>
              <input className="form-control" 
               value={adresse} onChange={(e) => setAdresse(e.target.value)}/>
            </div>

            <div className="mb-3">
              <label>Numéro de téléphone</label>
              <input className="form-control" 
               value={numéro} onChange={(e) => setNuméro(e.target.value)}/>
            </div>

            

            <div className="mb-3">
              <label>Devise</label>
              <select className="form-control"  value={devise} onChange={(e) => setDevise(e.target.value)}>
                <option>FXOF</option>
                <option>Dollar</option>
                <option>EUro</option>
                <option>FCFA</option>
              </select>
            </div>
           </div>
          </div>

             <input
              type="file"
              className="form-control p-5"
              accept="image/*"
              onChange={(e) => setImage(e.target.files[0])}
            />

          <div className="modal-footer">
            <button className="btn btn-secondary" onClick={onClose}>Annuler</button>
            <button className="btn btn-primary" onClick={handleSubmit}>Enregistrer</button>
          </div>

        </div>
      </div>
    </div>
  );
};

export default AddHotelModal;
