import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [email, setEmail] = useState("");
  const [passWord, setPassWord] = useState("");
  const [activeForm, setActiveForm] = useState("login"); // "login", "forgot", "signup"
  const navigate = useNavigate();

  // Login
  const handleSubmit = (e) => {
    e.preventDefault();
    if (email === "idy@gmail.com" && passWord === "1234") {
      localStorage.setItem("isAuth", "true");
      navigate("/dashboard");
    } else {
      alert("Email ou mot de passe incorrect");
    }
  };

  // Mot de passe oublié
  const handleForgot = (e) => {
    e.preventDefault();
    alert("Un email de réinitialisation a été envoyé !");
    setActiveForm("login"); // revenir au login
  };

  // Inscription
  const handleSignup = (e) => {
    e.preventDefault();
    alert("Compte créé avec succès !");
    setActiveForm("login"); // revenir au login
  };

  return (
    <div style={{width:"384px", margin:"auto", marginTop:"5%"}}>
      <div className="p-4 rounded ">

        {activeForm === "login" && (
          <>
          <div style={{border:"1px solid", padding:"10px", backgroundColor:"#555555"}}>
            <h3 className="text-center text-white mb-4">RED Product</h3>
          </div>
           <form onSubmit={handleSubmit} className="bg-white p-3">
            <h5 className=" mb-4 text-center">Connectez-vous en tant qu'Admin</h5>
           
              <div className="mb-3">
                <input
                  type="email"
                  className="form-control"
                  placeholder="E-mail"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <div className="mb-3">
                <input
                  type="password"
                  className="form-control"
                  placeholder="Mot de passe"
                  value={passWord}
                  onChange={(e) => setPassWord(e.target.value)}
                  required
                />
              </div>
              <div className="text-center">
                <button className="btn btn-dark w-100" type="submit" style={{backgroundColor:"#555555"}}>
                  Se connecter
                </button>
              </div>
            </form>

            <div className="text-center mt-3">
              <p
                className="text-primary cursor-pointer"
                onClick={() => setActiveForm("forgot")}
                style={{ cursor: "pointer", }}
              >
                Mot de passe oublié ?
              </p>
              <p>
                Vous n'avez pas de compte ?{" "}
                <span
                  className="text-primary"
                  style={{ cursor: "pointer" }}
                  onClick={() => setActiveForm("signup")}
                >
                  S'inscrire
                </span>
              </p>
            </div>
          </>
        )}

        {activeForm === "forgot" && (
          <>
            <h5 className="mb-4 text-center">Réinitialiser le mot de passe</h5>
            <form onSubmit={handleForgot}>
              <div className="mb-3">
                <input
                  type="email"
                  className="form-control"
                  placeholder="Votre e-mail"
                  required
                />
              </div>
              <div className="text-center">
                <button className="btn btn-primary w-100" type="submit">
                  Envoyer le lien
                </button>
              </div>
            </form>
            <p
              className="text-center mt-3 text-secondary"
              style={{ cursor: "pointer" }}
              onClick={() => setActiveForm("login")}
            >
              Retour au login
            </p>
          </>
        )}

        {activeForm === "signup" && (
          <>
            <h5 className="mb-4 text-center">Créer un compte</h5>
            <form onSubmit={handleSignup}>
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Nom complet"
                  required
                />
              </div>
              <div className="mb-3">
                <input
                  type="email"
                  className="form-control"
                  placeholder="E-mail"
                  required
                />
              </div>
              <div className="mb-3">
                <input
                  type="password"
                  className="form-control"
                  placeholder="Mot de passe"
                  required
                />
              </div>
              <div className="text-center">
                <button className="btn btn-success w-100" type="submit">
                  S'inscrire
                </button>
              </div>
            </form>
            <p
              className="text-center mt-3 text-secondary"
              style={{ cursor: "pointer" }}
              onClick={() => setActiveForm("login")}
            >
              Retour au login
            </p>
          </>
        )}

      </div>
    </div>
  );
};

export default Login;
