const Dashboard = () => {
  const stats = [
    { title: "Formulaires", value: 125, icon: "üìÑ", color: "primary" },
    { title: "Messages", value: 48, icon: "üí¨", color: "success" },
    { title: "Emails", value: 32, icon: "üìß", color: "warning" },
    { title: "H√¥tels", value: 8, icon: "üè®", color: "danger" },
  ];

  return (
    <div>
      <h2 className="mb-4">Dashboard</h2>

      <div className="row">
        {stats.map((item, index) => (
          <div className="col-md-3 mb-3" key={index}>
            <div className={`card text-white bg-${item.color} h-100`}>
              <div className="card-body">
                <div className="d-flex justify-content-between align-items-center">
                  <div>
                    <h5 className="card-title">{item.title}</h5>
                    <h2>{item.value}</h2>
                  </div>
                  <div style={{ fontSize: "40px" }}>{item.icon}</div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
