import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [passWord, setPassWord] = useState("");
  const [activeForm, setActiveForm] = useState("login");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (email === "idy@gmail.com" && passWord === "1234") {
      localStorage.setItem("isAuth", "true");
      navigate("/dashboard");
    } else {
      alert("Email ou mot de passe incorrect");
    }
  };

  const handleForgot = (e) => {
    e.preventDefault();
    alert("Un email de réinitialisation a été envoyé !");
    setActiveForm("login");
  };

  const handleSignup = (e) => {
    e.preventDefault();
    alert("Compte créé avec succès !");
    setActiveForm("login");
  };

  return (
    <div className="login-bg">
      <div className="login-box">

        <div className="login-header">
          <h3>RED PRODUCT</h3>
        </div>

        {activeForm === "login" && (
          <form onSubmit={handleSubmit} className="login-form">
            <h5 className="text-center mb-4">Connexion Admin</h5>
            <input
              type="email"
              className="form-control input-dark mb-3"
              placeholder="E-mail"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              className="form-control input-dark mb-4"
              placeholder="Mot de passe"
              value={passWord}
              onChange={(e) => setPassWord(e.target.value)}
            />
            <button className="btn btn-warning w-100">Se connecter</button>

            <div className="text-center mt-3">
              <span className="link" onClick={() => setActiveForm("forgot")}>
                Mot de passe oublié ?
              </span>
              <br />
              <span className="link" onClick={() => setActiveForm("signup")}>
                Créer un compte
              </span>
            </div>
          </form>
        )}

        {activeForm === "forgot" && (
         <form onSubmit={handleSubmit} className="login-form">
  <p className="subtitle">Connectez-vous en tant que Admin</p>

  <input
    type="email"
    className="input"
    placeholder="E-mail"
    value={email}
    onChange={(e) => setEmail(e.target.value)}
  />

  <input
    type="password"
    className="input"
    placeholder="Mot de passe"
    value={passWord}
    onChange={(e) => setPassWord(e.target.value)}
  />

  <div className="remember">
    <input type="checkbox" id="remember" />
    <label htmlFor="remember">Gardez-moi connecté</label>
  </div>

  <button className="btn-login">Se connecter</button>

  <div className="links">
    <span onClick={() => setActiveForm("forgot")}>Mot de passe oublié?</span>
    <p>
      Vous n'avez pas de compte?{" "}
      <b onClick={() => setActiveForm("signup")}>S'inscrire</b>
    </p>
  </div>
</form>

        )}

      </div>
    </div>
  );
};

export default Login;
