const Dashboard = () => {
  const stats = [
    { title: "Formulaires", value: 125, icon: <i class="bi bi-envelope-open"></i>, color:"danger"},
    { title: "Messages", value: 40, icon: <i class="bi bi-envelope-open"></i>, color:"success opacity-50" },
    { title: "Emails", value: 25, icon: <i class="bi bi-envelope-open"></i>, color:"danger" },
    { title: "Hôtels", value: 40, icon: <i class="bi bi-envelope-open"></i>, color:"danger" },
  ];

  return (
    <div>
      <h3 className="mb-4">Bienvenues sur RED Produit </h3>

      <div className="row">
        {stats.map((item, index) => (
          <div className="col-md-6 mb-3" key={index}>
            <div className={`card text-dark h-100`}>
              <div className="card-body">
                  <div className="d-flex text-secondary gap-2 align-items-center">
                    <div className={`bg-${item.color} p-3 h6 text-white rounded-circle`} style={{fontSize:"bold",}}>  {item.icon} </div>
                     <p className="card-title"><span className="h2"> {item.value} </span> {item.title}
                      <br />  je ne sais pas quoi mettre
                     </p> 
                     
                   
                  </div>
                  
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
