import React from 'react'
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
    const [ email, setEmail ] = useState("");
    const [ passWord, setPassWord ] = useState("");
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();

        if (email === "idy@gmail.com" && passWord === "1234") {
            localStorage.setItem("isAuth", "true");
            navigate("/dashboard");
        } else {
            alert("Email ou mot de passe incorrect");
        }
    };
  return (
    <div className='container'>
        <div className='row justify-content-center align-item-center bg-white w-50 mx-auto mt-5 p-5' style={{height:"30px"}}>
            <div className='col'>
                <h6 className='mb-4'>Connectez-vous en tant que Admin</h6>


                <form onSubmit={handleSubmit}>
                    <div className='mb-3'>
                        <input type="email" className='form-control' placeholder='E-mail'
                               value={email} 
                               onChange={ (e) => setEmail(e.target.value) } 
                        />
                    </div>

                    <div className='mb-3'>
                        <input type="passWord" className='form-control' placeholder='Mot de passe'
                               value={passWord} 
                               onChange={ (e) => setPassWord(e.target.value) } 
                        />
                    </div>

                   <div className='text-center'>
                     <button className='btn bg-dark text-white w-50' type='submit'>Se connecter</button>
                   </div>
                </form>
            </div>
        </div>

        <div className='d-flex flex-column justify-content-center mt-4'>
            <p className='text-center'>Mot de passe oubli√©?</p>
            <p  className='text-center'>Vous n'avez pas de compte? 
                <span>S'inscrire</span>
            </p>
        </div>

    </div>
  )
}

export default Login
