import Navbar from "./components/Navbar";
import Sidebar from "./components/Sidebar";
import { useLocation } from "react-router-dom";

const Layout = ({ children }) => {
  const location = useLocation();

  let title = "";
  let description = "";

  if (location.pathname === "/dashboard") {
    title = "Bienvenue sur RED Produit";
    description = "Tableau de bord de gestion générale";
  } else if (location.pathname === "/hotels") {
    title = "Liste des Hôtels";
    description = "Consultez et gérez tous les hôtels enregistrés";
  }

  return (
    <>
      <Navbar title={title} />

      {/* Header sous la navbar */}
      <div className="bg-white px-4 py-3 border-bottom">
        <p className="mb-0">
          <span className="h3 text-dark" style={{ fontWeight: "100" }}>
            {title}
          </span>
          <br />
          {description}
        </p>
      </div>

      <div className="d-flex">
        <Sidebar />
        <div className="p-4 flex-grow-1">
          {children}
        </div>
      </div>
    </>
  );
};

export default Layout;
