import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [passWord, setPassWord] = useState("");
  const [activeForm, setActiveForm] = useState("login");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (email === "idy@gmail.com" && passWord === "1234") {
      localStorage.setItem("isAuth", "true");
      navigate("/dashboard");
    } else {
      alert("Email ou mot de passe incorrect");
    }
  };

  const handleForgot = (e) => {
    e.preventDefault();
    alert("Un email de réinitialisation a été envoyé !");
    setActiveForm("login");
  };

  const handleSignup = (e) => {
    e.preventDefault();
    alert("Compte créé avec succès !");
    setActiveForm("login");
  };

  return (
    <div className="login-bg d-flex flex-column">
       <div className="login-header">
          <h3>RED PRODUCT</h3>
        </div>
      <div className="login-box">
        
       

        {activeForm === "login" && (
          <form onSubmit={handleSubmit} className="login-form">
            <h5 className="text-center mb-4">Connectez vous entant que admin</h5>
            <input
              type="email"
             className="form-control input-dark mb-4"
              placeholder="E-mail"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              className="form-control input-dark mb-4 border-bottom"
              placeholder="Mot de passe"
              value={passWord}
              onChange={(e) => setPassWord(e.target.value)}
            />
            <p className="d-flex align-items-center h6 gap-2"><input type="checkbox" /> Gardez-moi connecté</p>
            <button className="btn w-100 text-white" style={{background: "#555555"}}>Se connecter</button>
          </form>

        )}
       

        {activeForm === "forgot" && (
          <form onSubmit={handleForgot} className="login-form">
            <h5 className="text-center mb-4">Mot de passe oublié</h5>
            <input
              type="email"
              className="form-control input-dark mb-4"
              placeholder="Votre e-mail"
            />
            <button className="btn btn-warning w-100">Envoyer</button>
            <div className="text-center mt-3">
              <span className="link" onClick={() => setActiveForm("login")}>
                Retour
              </span>
            </div>
          </form>
        )}

        {activeForm === "signup" && (
          <form onSubmit={handleSignup} className="login-form">
            <h5 className="text-center mb-4">Créer un compte</h5>
            <input type="text" className="form-control input-dark mb-3" placeholder="Nom complet" />
            <input type="email" className="form-control input-dark mb-3" placeholder="E-mail" />
            <input type="password" className="form-control input-dark mb-4" placeholder="Mot de passe" />
            <button className="btn btn-warning w-100">S'inscrire</button>
            <div className="text-center mt-3">
              <span className="link" onClick={() => setActiveForm("login")}>
                Retour
              </span>
            </div>
          </form>
        )}
      </div>
       <div className="text-center mt-3">
          <p className="link d-block mb-2" onClick={() => setActiveForm("forgot")}>
            Mot de passe oublié ?
          </p>
          <p className="d-flex align-items-center gap-1 "> Vous n'avais pas de compte?
             <span className="link d-block" onClick={() => setActiveForm("signup")}>s'inscrir</span>
          </p>
        </div>
    </div>
  );
};

export default Login;
