import { useState } from "react";

const AddHotelModal = ({ show, onClose, onAdd }) => {
  const [nom, setNom] = useState("");
  const [ville, setVille] = useState("");
  const [prix, setPrix] = useState("");
  const [etoiles, setEtoiles] = useState("1");
  const [image, setImage] = useState(null);

  if (!show) return null;

  const handleSubmit = () => {
    if (!nom || !ville || !prix) {
      alert("Veuillez remplir tous les champs");
      return;
    }

    const newHotel = {
      id: Date.now(),
      nom,
      ville,
      prix,
      etoiles: Number(etoiles),
      image: image ? URL.createObjectURL(image) : "/images/default-hotel.jpg",
    };

    onAdd(newHotel);
    onClose();

    // Reset
    setNom("");
    setVille("");
    setPrix("");
    setEtoiles("1");
    setImage(null);
  };

  return (
    <div className="modal fade show d-block" style={{ background: "rgba(0,0,0,0.5)" }}>
      <div className="modal-dialog">
        <div className="modal-content">

          <div className="modal-header">
            <h5 className="modal-title">Créer un nouvel hôtel</h5>
            <button className="btn-close" onClick={onClose}></button>
          </div>

          <div className="modal-body">
            <input className="form-control mb-2" placeholder="Nom"
              value={nom} onChange={(e) => setNom(e.target.value)} />

            <input className="form-control mb-2" placeholder="Ville"
              value={ville} onChange={(e) => setVille(e.target.value)} />

            <input className="form-control mb-2" type="number" placeholder="Prix"
              value={prix} onChange={(e) => setPrix(e.target.value)} />

            <select className="form-control mb-2"
              value={etoiles} onChange={(e) => setEtoiles(e.target.value)}>
              <option>1</option><option>2</option><option>3</option>
              <option>4</option><option>5</option>
            </select>

            <input
              type="file"
              className="form-control"
              accept="image/*"
              onChange={(e) => setImage(e.target.files[0])}
            />
          </div>

          <div className="modal-footer">
            <button className="btn btn-secondary" onClick={onClose}>Annuler</button>
            <button className="btn btn-primary" onClick={handleSubmit}>
              Enregistrer
            </button>
          </div>

        </div>
      </div>
    </div>
  );
};

export default AddHotelModal;
