import Navbar from "./components/Navbar";
import Sidebar from "./components/Sidebar";
import { useLocation } from "react-router-dom";

const Layout = ({ children }) => {
  const location = useLocation();

  let title = "";
  let description = "";

  if (location.pathname === "/dashboard") {
    title = "Lorem ipsum dolor sit amet consecteur ";
    description = "Bienvenue sur RED Produit";
  } else if (location.pathname === "/hotels") {
    title = "H么tels";
    description = "Gestion de la liste des h么tels";
  }

  return (
    <div className="d-flex" style={{ minHeight: "100vh" }}>
      
      {/* SIDEBAR */}
      <div style={{ width: "300px", minWidth: "300px" }} // <- largeur fixe
           className="bg-dark d-flex flex-column">
        <Sidebar />
      </div>

      {/* ZONE DROITE */}
      <div className="flex-grow-1 d-flex flex-column">

        {/* NAVBAR */}
        <div className="border-bottom">
          <Navbar title={title} />
        </div>

        {/* HEADER */}
        <div className="bg-white px-4 py-3 border-bottom">
          <p className="mb-0">
            <span className="h4 text-dark fw-light">{title}</span>
            <br />
            <small className="text-muted">{description}</small>
          </p>
        </div>

        {/* CONTENU */}
        <div className="p-4 flex-grow-1 bg-light">
          {children}
        </div>

      </div>
    </div>
  );
};

export default Layout;
